let gameoflife = document.querySelector('#gameoflife');
gameoflife.width = window.innerWidth;
gameoflife.height = window.innerHeight;
let ctx = gameoflife.getContext('2d');

let squareSize = window.innerWidth / 60;

let width = Math.ceil(window.innerWidth / squareSize);
let height = Math.ceil(window.innerHeight / squareSize);
console.log(width, height);

const create2DArray = (width, height) => {
  let arr = [];
  for (let i = 0; i < width; i++) {
      arr[i] = [];
      for (let j = 0; j < height; j++) {
          arr[i][j] = 0;
      }
  }
  return arr;
}


