//checks if the cell should live or die in the game of life
const nextGen = (x, y) => {
  let neighbors = 0;
  for (let i = -1; i <= 1; i++) {
    for (let j = -1; j <= 1; j++) {
      if (i === 0 && j === 0) {
        continue;
      }
      if (grid[x + i] && grid[x + i][y + j]) {
        neighbors += grid[x + i][y + j];
      }
    }
  }
  if (grid[x][y] === 1 && (neighbors < 2 || neighbors > 3)) {
    return 0;
  } else if (grid[x][y] === 0 && neighbors === 3) {
    return 1;
  } else {
    return grid[x][y];
  }
} 
//given a board in the game of life, it will return the next generation of the board
const update = (grid) => {
  let newGrid = create2DArray(width, height);
  for (let i = 0; i < width; i++) {
    for (let j = 0; j < height; j++) {
      newGrid[i][j] = nextGen(i, j);
    }
  }
  return newGrid;
}

const render = (shouldUpdate) => {
  ctx.clearRect(0, 0, gameoflife.width, gameoflife.height);
  if(shouldUpdate) {
    let newGrid = update(grid);
    grid = newGrid;
  }
  for (let i = 0; i < width; i++) {
    for (let j = 0; j < height; j++) {
      //draws the cells if they are alive
      if (grid[i][j] === 1) {
        ctx.fillStyle = "#87CEEB"
        ctx.fillRect(i * squareSize, j * squareSize, squareSize, squareSize);
      }
    }
  }

  // Draw pattern preview
  if (editMode && selectedPattern && previewX >= 0 && previewY >= 0) {
    const pattern = patterns[selectedPattern];
    ctx.fillStyle = "rgba(255, 157, 0, 0.6)"; // Orange preview color
    for (let y = 0; y < pattern.length; y++) {
      for (let x = 0; x < pattern[y].length; x++) {
        if (pattern[y][x] === 1) {
          const gridX = previewX + x;
          const gridY = previewY + y;
          if (gridX >= 0 && gridX < width && gridY >= 0 && gridY < height) {
            ctx.fillRect(gridX * squareSize, gridY * squareSize, squareSize, squareSize);
          }
        }
      }
    }
  }
}

