let editMode = false;
let selectedPattern = null;
let isDrawing = false;
let drawValue = 1;
let previewX = -1;
let previewY = -1;

const editBtn = document.getElementById('gol-edit');
const patternsBtn = document.getElementById('gol-patterns-btn');
const patternsMenu = document.getElementById('gol-patterns-menu');
const patternsContainer = document.getElementById('gol-patterns');

const placePattern = (pattern, startX, startY) => {
  for (let y = 0; y < pattern.length; y++) {
    for (let x = 0; x < pattern[y].length; x++) {
      const gridX = startX + x;
      const gridY = startY + y;
      if (gridX >= 0 && gridX < width && gridY >= 0 && gridY < height) {
        grid[gridX][gridY] = pattern[y][x];
      }
    }
  }
  render(false);
};

const getGridCoords = (event) => {
  const x = Math.floor(event.offsetX / window.innerWidth * width);
  const y = Math.floor(event.offsetY / window.innerHeight * height);
  return { x, y };
};

gameoflife.addEventListener("mousedown", (event) => {
  if (!editMode) return;

  const { x, y } = getGridCoords(event);

  if (selectedPattern) {
    placePattern(patterns[selectedPattern], x, y);
    selectedPattern = null;
    previewX = -1;
    previewY = -1;
    gameoflife.style.cursor = 'crosshair';
  } else {
    isDrawing = true;
    drawValue = grid[x][y] === 0 ? 1 : 0;
    grid[x][y] = drawValue;
    render(false);
  }
});

gameoflife.addEventListener("mousemove", (event) => {
  if (!editMode) return;

  const { x, y } = getGridCoords(event);

  // Update pattern preview position
  if (selectedPattern) {
    previewX = x;
    previewY = y;
    render(false);
    return;
  }

  // Handle drawing
  if (isDrawing && x >= 0 && x < width && y >= 0 && y < height) {
    grid[x][y] = drawValue;
    render(false);
  }
});

gameoflife.addEventListener("mouseup", () => {
  isDrawing = false;
});

gameoflife.addEventListener("mouseleave", () => {
  isDrawing = false;
});

editBtn.addEventListener('click', () => {
  editMode = !editMode;
  editBtn.classList.toggle('active', editMode);
  gameoflife.classList.toggle('edit-mode', editMode);
  document.body.classList.toggle('gol-edit-mode', editMode);
  patternsContainer.style.display = editMode ? 'block' : 'none';

  if (editMode) {
    stopSimulation();
  }
});

patternsBtn.addEventListener('click', (e) => {
  e.stopPropagation();
  patternsMenu.classList.toggle('show');
});

patternsMenu.querySelectorAll('button').forEach(btn => {
  btn.addEventListener('click', (e) => {
    e.stopPropagation();
    selectedPattern = btn.dataset.pattern;
    patternsMenu.classList.remove('show');
    gameoflife.style.cursor = 'copy';
  });
});

document.addEventListener('click', (e) => {
  if (!patternsContainer.contains(e.target)) {
    patternsMenu.classList.remove('show');
  }
});

render(false);

let isPlaying = true;
let intervalId = null;

const stepBtn = document.getElementById('gol-step');
const toggleBtn = document.getElementById('gol-toggle');
const clearBtn = document.getElementById('gol-clear');
const landerContent = document.querySelector('.lander-content');

const tick = () => {
  counter++;
  render(true);
};

const startSimulation = () => {
  if (!intervalId) {
    intervalId = setInterval(tick, 100);
  }
  isPlaying = true;
  toggleBtn.textContent = 'Pause';
};

const stopSimulation = () => {
  if (intervalId) {
    clearInterval(intervalId);
    intervalId = null;
  }
  isPlaying = false;
  toggleBtn.textContent = 'Play';
};

toggleBtn.addEventListener('click', () => {
  if (isPlaying) {
    stopSimulation();
  } else {
    startSimulation();
  }
});

stepBtn.addEventListener('click', () => {
  stopSimulation();
  render(true);
});

clearBtn.addEventListener('click', () => {
  grid = create2DArray(width, height);
  counter = 0;
  render(false);
});

// Hide patterns menu initially
patternsContainer.style.display = 'none';

startSimulation();

// Project row expand functionality
document.querySelectorAll('.proj').forEach(proj => {
  proj.addEventListener('click', (e) => {
    // Don't toggle if clicking a link or thumbnail
    if (e.target.tagName === 'A') return;
    if (e.target.closest('.proj-thumbs')) return;

    // Close other expanded projects
    document.querySelectorAll('.proj.expanded').forEach(other => {
      if (other !== proj) {
        other.classList.remove('expanded');
      }
    });

    // Toggle current project
    proj.classList.toggle('expanded');
  });
});

// Gallery thumbnail click functionality
document.querySelectorAll('.proj-thumbs img').forEach(thumb => {
  thumb.addEventListener('click', (e) => {
    e.stopPropagation();
    const gallery = thumb.closest('.proj-gallery');
    const mainImg = gallery.querySelector('.proj-main-img');

    // Update main image
    mainImg.src = thumb.src;
    mainImg.alt = thumb.alt;

    // Update active state
    gallery.querySelectorAll('.proj-thumbs img').forEach(t => t.classList.remove('active'));
    thumb.classList.add('active');
  });
});

// Image modal functionality
const imageModal = document.getElementById('image-modal');
const modalImg = imageModal.querySelector('.modal-img');
const modalClose = imageModal.querySelector('.modal-close');

document.querySelectorAll('.proj-main-img').forEach(img => {
  img.addEventListener('click', (e) => {
    e.stopPropagation();
    modalImg.src = img.src;
    modalImg.alt = img.alt;
    imageModal.classList.add('show');
  });
});

modalClose.addEventListener('click', () => {
  imageModal.classList.remove('show');
});

imageModal.addEventListener('click', (e) => {
  if (e.target === imageModal) {
    imageModal.classList.remove('show');
  }
});

document.addEventListener('keydown', (e) => {
  if (e.key === 'Escape' && imageModal.classList.contains('show')) {
    imageModal.classList.remove('show');
  }
});

// Gravity game edit/return button functionality
const gravityPlayBtn = document.getElementById('gravity-play-btn');
const gravityResetBtn = document.getElementById('gravity-reset-btn');
const projectsSection = document.querySelector('.projects');
const gravityIframe = document.querySelector('.gravity-bg');

if (gravityPlayBtn && projectsSection) {
  gravityPlayBtn.addEventListener('click', () => {
    const isPlaying = projectsSection.classList.toggle('playing');
    gravityPlayBtn.textContent = isPlaying ? 'Return' : 'Edit';
    document.body.style.overflow = isPlaying ? 'hidden' : '';
    if (isPlaying && gravityIframe) {
      gravityIframe.focus();
    }
  });
}

if (gravityResetBtn && gravityIframe) {
  gravityResetBtn.addEventListener('click', () => {
    gravityIframe.src = gravityIframe.src;
  });
}
